class Solution {
public:
    int helper(vector<vector<int>>& grid,int rr1,int rr2,int cc1,int cc2) {
        int m=rr2-rr1,n=cc2-cc1;
        // cout<<rr1<<','<<rr2<<','<<cc1<<','<<cc2<<endl;
        // cout<<m<<','<<n<<endl;
        // return 0;
        vector<int> row(m),col(n);
        for(int i=0;i<m;++i){
            for(int j=0;j<n;++j){
                row[i]+=grid[i+rr1][j+cc1];
            }
        }
        for(int j=0;j<n;++j){
            for(int i=0;i<m;++i){
                col[j]+=grid[i+rr1][j+cc1];
            }
        }
        int c1=0,c2=0;
        for(int j=0;j<n;++j){
            if(col[j]>0){
                c1=j;
                break;
            }
        }
        for(int j=n-1;j>0;--j){
            if(col[j]>0){
                c2=j;
                break;
            }
        }
        if(c2<c1) return 1;
        int r1=0,r2=0;
        for(int j=0;j<m;++j){
            if(row[j]>0){
                r1=j;
                break;
            }
        }
        for(int j=m-1;j>0;--j){
            if(row[j]>0){
                r2=j;
                break;
            }
        }
        if(r2<r1) return 1;
        return (r2-r1+1)*(c2-c1+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size(), n=grid[0].size();
        int ans=m*n;
        for(int i=1;i<m;++i){
            for(int j=1;j<n;++j){
                ans = min(ans,helper(grid,0,i,0,j)+
                helper(grid,0,i,j,n)+helper(grid,i,m,0,n));
                ans = min(ans,helper(grid,i,m,0,j)+
                helper(grid,i,m,j,n)+helper(grid,0,i,0,n));
            }
        }
        for(int j=1;j<n;++j){
            for(int i=1;i<m;++i){
                ans = min(ans,helper(grid,0,i,0,j)+
                helper(grid,i,m,0,j)+helper(grid,0,m,j,n));
                ans = min(ans,helper(grid,0,i,j,n)+
                helper(grid,i,m,j,n)+helper(grid,0,m,0,j));
            }
        }
        for(int i1=1;i1<m;++i1){
            for(int i2=i1+1;i2<m;++i2){
                ans = min(ans,helper(grid,0,i1,0,n)+
                helper(grid,i1,i2,0,n)+helper(grid,i2,m,0,n));
            }
        }
        for(int j1=1;j1<n;++j1){
            for(int j2=j1+1;j2<n;++j2){
                ans = min(ans,helper(grid,0,m,0,j1)+
                helper(grid,0,m,j1,j2)+helper(grid,0,m,j2,n));
            }
        }
        return ans;
    }
};
