class Solution {
public:
    int sum(vector<vector<int>>& grid, int x, int y, int n, int m){
        vector<int> a, b;
        for(int i=x; i<n; i++){
            for(int j=y; j<m; j++){
                if(grid[i][j]){
                    a.push_back(i);
                    b.push_back(j);
                }
            }
        }
        sort(b.begin(), b.end());
        if(a.empty())return(0);
        return((a.back()-a[0]+1)*(b.back()-b[0]+1));
    }
    int minimumSum(vector<vector<int>>& grid) {
        int ans = 900;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0; i<n; i++){
            for(int j=i; j<n; j++){
                ans=min(ans, sum(grid, 0, 0, i, m)+sum(grid, i, 0, j+1, m)+sum(grid, j+1, 0, n, m));
            }
        }
        for(int i=0; i<m; i++){
            for(int j=i; j<m; j++){
                ans=min(ans, sum(grid, 0, 0, n, i)+sum(grid, 0, i, n, j+1)+sum(grid, 0, j+1, n, m));
            }
        }
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                ans=min(ans, sum(grid, 0, 0, i+1, m)+sum(grid, i+1, 0, n, j+1)+sum(grid, i+1, j+1, n, m));
                ans=min(ans, sum(grid, i+1, 0, n, m)+sum(grid, 0, 0, i+1, j+1)+sum(grid, 0, j+1, i+1, m));
            }
        }
        for(int i=0; i<m; i++){
            for(int j=0; j<n; j++){
                ans=min(ans, sum(grid, 0, 0, n, i+1)+sum(grid, 0, i+1, j+1, m)+sum(grid, j+1, i+1, n, m));
                ans=min(ans, sum(grid, 0, i+1, n, m)+sum(grid, 0, 0, j+1, i+1)+sum(grid, j+1, 0, n, i+1));
            }
        }
        return(ans);
    }
};
