class Solution {
public:
    int solve(vector<vector<int>> grid, int s) {
        if(s == 1) {
            int x = grid.size() + 5;
            int y = grid[0].size() + 5;
            int u = 0;
            int v = 0;
            for(int i = 0; i < grid.size(); i++) {
                for(int j = 0; j < grid[0].size(); j++) {
                    if(grid[i][j] == 1) {
                        x = min(x, i);
                        y = min(y, j);
                        u = max(u, i);
                        v = max(v, j);
                    }
                }
            }
            if(x > u) return 1;
            return (u - x + 1) * (v - y + 1);
        }

        int n = grid.size();
        int m = grid[0].size();

        int ans = 1e9;
        for(int i = 0; i + 1 < n; i++) {
            vector<vector<int>> grid1, grid2;
            for(int x = 0; x < n; x++) {
                if(x <= i) grid1.push_back(grid[x]);
                else grid2.push_back(grid[x]);
            }
            if(s == 3) {
                int res = min(solve(grid1, 2) + solve(grid2, 1), solve(grid1, 1) + solve(grid2, 2));
                ans = min(ans, res);
            } else {
                ans = min(ans, solve(grid1, 1) + solve(grid2, 1));
            }
        }

        for(int j = 0; j + 1 < m; j++) {
            vector<vector<int>> grid1, grid2;
            for(int x = 0; x < n; x++) {
                grid1.push_back({});
                grid2.push_back({});
                for(int y = 0; y < m; y++) {
                    if(y <= j) grid1[x].push_back(grid[x][y]);
                    else grid2[x].push_back(grid[x][y]);
                }
            }
            if(s == 3) {
                int res = min(solve(grid1, 2) + solve(grid2, 1), solve(grid1, 1) + solve(grid2, 2));
                ans = min(ans, res);
            } else {
                ans = min(ans, solve(grid1, 1) + solve(grid2, 1));
            }
        }
        return ans;
    }
    int minimumSum(vector<vector<int>>& grid) {
        return solve(grid, 3);
    }
};
