class Solution {
    int h(vector<vector<int>>& grid, int _l, int _r, int _t, int _b) {
        int l=_r, r=_l, t=_b, b=_t;
        bool f = 0;
        for(int i=_l; i<=_r; i++) {
            for(int j=_t; j<=_b; j++) {
                if(grid[i][j] == 1) {
                    f = 1;
                    l = min(l, i);
                    r = max(r, i);
                    t = min(t, j);
                    b = max(b, j);
                }
            }
        }
        if(f) return (r-l+1) * (b-t+1);
        return 0;
    }

public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        int res = n * m;

        for(int i=1; i<n; i++) {
            for(int j=1; j<m; j++) {
                res = min(res, h(grid, 0, i-1, 0, j-1) 
                                + h(grid, 0, i-1, j, m-1) 
                                + h(grid, i, n-1, 0, m-1));

                res = min(res, h(grid, i, n-1, 0, j-1) 
                                + h(grid, i, n-1, j, m-1) 
                                + h(grid, 0, i-1, 0, m-1));
            }

            for(int i2=i+1; i2<n; i2++) {
                res = min(res, h(grid, 0, i-1, 0, m-1) 
                                + h(grid, i, i2-1, 0, m-1)
                                + h(grid, i2, n-1, 0, m-1));
            }            
        }

        for(int j=1; j<m; j++) {
            for(int i=1; i<n; i++) {
                res = min(res, h(grid, 0, i-1, 0, j-1) 
                                + h(grid, i, n-1, 0, j-1) 
                                + h(grid, 0, n-1, j, m-1));

                res = min(res, h(grid, 0, n-1, 0, j-1) 
                                + h(grid, i, n-1, j, m-1) 
                                + h(grid, 0, i-1, j, m-1));
            }     
            for(int j2=j+1; j2<m; j2++) {
                res = min(res, h(grid, 0, n-1, 0, j-1) 
                                + h(grid, 0, n-1, j, j2-1) 
                                + h(grid, 0, n-1, j2, m-1));
            }       
        }

        return res;
    }
};
