/**
 * @param {number[][]} grid
 * @return {number}
 */
var minimumSum = function(grid) {
    const getVal = (i1, j1, i2, j2) => {
        let minI = i2;
        let minJ = j2;
        let maxI = i1;
        let maxJ = j1;
        let hasOne = false;
        for (let i = i1; i <= i2; i++) {
            for (let j = j1; j <= j2; j++) {
                if (grid[i][j] === 0) continue;
                hasOne = true;
                minI = Math.min(i, minI);
                minJ = Math.min(j, minJ);
                maxI = Math.max(i, maxI);
                maxJ = Math.max(j, maxJ);
            }
        }
        if (!hasOne) return 1;
        return (maxI - minI + 1) * (maxJ - minJ + 1);
    }
    const n = grid.length;
    const m = grid[0].length;
    let result = n * m;
    for (let i = 0; i + 1 < n; i++) {
        for (let j = i + 1; j + 1 < n; j++) {
            result = Math.min(result, getVal(0, 0, i, m - 1) + getVal(i + 1, 0, j, m - 1) + getVal(j + 1, 0, n - 1, m - 1));
        }
        for (let j = 0; j + 1 < m; j++) {
            result = Math.min(result, getVal(0, 0, i, m - 1) + getVal(i + 1, 0, n - 1, j) + getVal(i + 1, j + 1, n - 1, m - 1));
            result = Math.min(result, getVal(i + 1, 0, n - 1, m - 1) + getVal(0, 0, i, j) + getVal(0, j + 1, i, m - 1));
        }
    }
    for (let i = 0; i + 1 < m; i++) {
        for (let j = i + 1; j + 1 < m; j++) {
            result = Math.min(result, getVal(0, 0, n - 1, i) + getVal(0, i + 1, n - 1, j) + getVal(0, j + 1, n - 1, m - 1));
        }
        for (let j = 0; j + 1 < n; j++) {
            result = Math.min(result, getVal(0, 0, n - 1, i) + getVal(0, i + 1, j, m - 1) + getVal(j + 1, i + 1, n - 1, m - 1));
            result = Math.min(result, getVal(0, i + 1, n - 1, m - 1) + getVal(0, 0, j, i) + getVal(j + 1, 0, n - 1, i));
        }
    }
    return result;
};
