class Solution {
    int minimumSum(vector<vector<int>>& grid, int i_min, int i_max, int j_min, int j_max) {
        int iMin = i_max, iMax = i_min;
        int jMin = j_max, jMax = j_min;

        for (int i = i_min; i <= i_max; i++)
            for (int j = j_min; j <= j_max; j++)
                if (grid[i][j] == 1)
                {
                    iMin = min(iMin, i);
                    iMax = max(iMax, i);
                    jMin = min(jMin, j);
                    jMax = max(jMax, j);
                }

        if (iMin > iMax || jMin > jMax)
            return 0;

        return (iMax - iMin + 1) * (jMax - jMin + 1);
    }

public:
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int res = INT_MAX;

        for (int i = 0; i+1 < m; i++)
        {
            for (int j = 0; j+1 < n; j++)
            {
                int a1 = minimumSum(grid, 0, i, 0, j);
                int a2 = minimumSum(grid, 0, i, j+1, n-1);
                int a3 = minimumSum(grid, i+1, m-1, 0, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }

            for (int j = 0; j+1 < n; j++)
            {
                int a1 = minimumSum(grid, 0, i, 0, n-1);
                int a2 = minimumSum(grid, i+1, m-1, 0, j);
                int a3 = minimumSum(grid, i+1, m-1, j+1, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }

            for (int j = i+1; j+1 < m; j++)
            {
                int a1 = minimumSum(grid, 0, i, 0, n-1);
                int a2 = minimumSum(grid, i+1, j, 0, n-1);
                int a3 = minimumSum(grid, j+1, m-1, 0, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }
        }

        for (int j = 0; j+1 < n; j++)
        {
            for (int i = 0; i+1 < m; i++)
            {
                int a1 = minimumSum(grid, 0, i, 0, j);
                int a2 = minimumSum(grid, i+1, m-1, 0, j);
                int a3 = minimumSum(grid, 0, m-1, j+1, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }

            for (int i = 0; i+1 < m; i++)
            {
                int a1 = minimumSum(grid, 0, m-1, 0, j);
                int a2 = minimumSum(grid, 0, i, j+1, n-1);
                int a3 = minimumSum(grid, i+1, m-1, j+1, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }

            for (int i = j+1; i+1 < n; i++)
            {
                int a1 = minimumSum(grid, 0, m-1, 0, j);
                int a2 = minimumSum(grid, 0, m-1, j+1, i);
                int a3 = minimumSum(grid, 0, m-1, i+1, n-1);

                if (a1 != 0 && a2 != 0 && a3 != 0)
                    res = min(res, a1+a2+a3);
            }
        }

        return res;
    }
};
