class Solution:

    def minimumSum(self, grid: List[List[int]]) -> int:
        def minArea(x1: int, y1: int, x2: int, y2: int) -> int:
            a, b, c, d = 10 ** 18, -1, 10 ** 18, -1
            for i in range(x1, x2 + 1):
                for j in range(y1, y2 + 1):
                    if grid[i][j] == 1:
                        a = min(a, i)
                        b = max(b, i)
                        c = min(c, j)
                        d = max(d, j)
            if a == 10 ** 18:
                return 1
            return (d - c + 1) * (b - a + 1)
        m, n = len(grid), len(grid[0])
        ans = 10 ** 18
        for i in range(m - 2):
            for j in range(i + 1, m - 1):
                t = minArea(0, 0, i, n - 1) + minArea(i + 1, 0, j, n - 1) + minArea(j + 1, 0, m - 1, n - 1)
                ans = min(ans, t)

        for i in range(n - 2):
            for j in range(i + 1, n - 1):
                t = minArea(0, 0, m - 1, i) + minArea(0, i + 1, m - 1, j) + minArea(0, j + 1, m - 1, n - 1)
                ans = min(ans, t)
        # 上1下2
        for i in range(m - 1):
            f = minArea(0, 0, i, n - 1)
            for j in range(n - 1):
                t = minArea(i + 1, 0, m - 1, j) + minArea(i + 1, j + 1, m - 1, n - 1)
                ans = min(ans, f + t)

        # 下1上2
        for i in range(1, m):
            f = minArea(i, 0, m - 1, n - 1)
            for j in range(n - 1):
                t = minArea(0, 0, i - 1, j) + minArea(0, j + 1, i - 1, n - 1)
                ans = min(ans, f + t)

        # 左1右2
        for i in range(n - 1):
            f = minArea(0, 0, m - 1, i)
            for j in range(m - 1):
                t = minArea(0, i + 1, j, n - 1) + minArea(j + 1, i + 1, m - 1, n - 1)
                ans = min(ans, f + t)
        # 左2右1
        for i in range(1, n):
            f = minArea(0, i, m - 1, n - 1)
            for j in range(m - 1):
                t = minArea(0, 0, j, i - 1) + minArea(j + 1, 0, m - 1, i - 1)
                ans = min(ans, f + t)

        return ans
