#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
#define SZ 666666
#define pb push_back
#define fi first
#define se second
#define mp make_pair
vector<vector<int>> s;
typedef pair<int,int> pii;
pii md(-1,-1);
void dfs(int x,int fa=-1,int d=0) {
    md=max(md,mp(d,x));
    for(auto y:s[x]) if(y!=fa) dfs(y,x,d+1);
}
int dia(vector<vector<int>> edg) {
    s.clear();
    int n=edg.size()+1;
    s.resize(n);
    for(auto p:edg) {
        int x=p[0],y=p[1];
        s[x].pb(y);
        s[y].pb(x);
    }
    md=mp(-1,-1);
    dfs(0);
    int x=md.se;
    md=mp(-1,-1);
    dfs(x);
    return md.fi;
}
class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int d1=dia(edges1),d2=dia(edges2);
        return max(d1,max(d2,(d1+1)/2+(d2+1)/2+1));
    }
};
