void dfs(int node, vector<int> adj[], int par, vector<int>& path) {

    for (auto child : adj[node]) {
        if (child == par) continue;
        path[child] = 1 + path[node];
        dfs(child, adj, node, path);
    }

}

int solve(int n,vector<int> adj[]) {
    // int n;
    // cin >> n;
    // vector<int> adj[n + 1];
    // for (int i = 0; i < n - 1; i++) {
    //     int u, v;
    //     cin >> u >> v;
    //     adj[v].push_back(u);
    //     adj[u].push_back(v);
    // }
    vector<int> path(n + 1, 0);
    dfs(1, adj, 0, path);
    // for (auto it : path)
    //     cout << it << " ";
    // cout << endl;
    int maxi = 0, lastnode = 1;
    for (int i = 1; i <= n; i++) {
        if (maxi < path[i]) {
            maxi = path[i];
            lastnode = i;
        }
    }
    path.assign(n + 1, 0);
    maxi = 0;
    dfs(lastnode, adj, 0, path);
    // cout << lastnode << endl;
    // for (auto it : path)
    //     cout << it << " ";
    for (int i = 1; i <= n; i++)
        maxi = max(maxi, path[i]);
    // cout << endl;
    // cout << maxi+1 << endl;
    return maxi+1;
}

class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size()+1;
        int m = edges2.size()+1;
        vector<int> adj1[n+1];
        vector<int> adj2[m+1];
        for(auto & x: edges1){
            int u = x[0]+1;
            int v = x[1]+1;
            adj1[u].push_back(v);
            adj1[v].push_back(u);

        }
        for(auto & x: edges2){
            int u = x[0]+1;
            int v = x[1]+1;
            adj2[u].push_back(v);
            adj2[v].push_back(u);

        }
        int a  = solve(n,adj1);
        int b  = solve(m,adj2);
        int p = a+2;
        int q = b+2;
        a--;
        b--;
        p /= 2;
        q /= 2;
        int maxi = max(a,b);
        return max(maxi,p+q-1);
    }
};
