typedef long long ll;
class Solution {
public:

    void dfs1(vector<ll> adj[], ll node, ll parent, ll cnt, vector<ll> &height){
        height[node] = cnt;
        for(auto it : adj[node]){
            if(parent != it){
                dfs1(adj,it,node,cnt+1,height);
            }
        }
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size();
        int m = edges2.size();
        vector<ll> adj1[n+1],adj2[m+1];
        for(int i=0; i<n; i++){
            adj1[edges1[i][0]].push_back(edges1[i][1]);
            adj1[edges1[i][1]].push_back(edges1[i][0]);
        }
        for(int i=0; i<m; i++){
            adj2[edges2[i][0]].push_back(edges2[i][1]);
            adj2[edges2[i][1]].push_back(edges2[i][0]);
        }
        vector<ll> height1(n+1,0);
		dfs1(adj1,0,-1,0,height1);
		ll far1 = 0;
		ll node1 = 0; 
		for(int i=0; i<=n; i++){
			if(height1[i] > far1){
				node1 = i;
				far1 = height1[i];
			}
		}
		vector<ll> dis1(n+1,0);
		dfs1(adj1,node1,-1,0,dis1);
		ll far2 = 0;
		ll node2 = far1;
		for(int i=0; i<=n; i++){
			if(dis1[i] > far2){
				far2 = dis1[i];
				node2 = i;
			}
		}
        int val1 = far2;



        vector<ll> height2(m+1,0);
		dfs1(adj2,0,-1,0,height2);
		far1 = 0;
		node1 = 0; 
		for(int i=0; i<=m; i++){
			if(height2[i] > far1){
				node1 = i;
				far1 = height2[i];
			}
		}
		vector<ll> dis2(m+1,0);
		dfs1(adj2,node1,-1,0,dis2);
		far2 = 0;
		node2 = far1;
		for(int i=0; i<=m; i++){
            // cout<<dis2[i]<<endl;
			if(dis2[i] > far2){
				far2 = dis2[i];
				node2 = i;
			}
		}
        int val2 = far2;


        int ans = ceil(1.0 * val1/2 * 1.0) + ceil(1.0 * val2/2* 1.0);
        return max({val1,val2,ans+1});
    }
};
