class Solution {
private:
    pair<int,int> dfs(int node, vector<bool> &vis, vector<vector<int>> &adj){
        vis[node] = 1;
        int dis = 0;
        int last = node;
        for(auto it:adj[node]){
            if(vis[it]) continue;
            auto p = dfs(it,vis,adj);
            if(p.second>dis){
                last = p.first;
                dis = p.second; 
            }
        }
        return {last,dis+1};
    }
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size()+1;
        int m = edges2.size()+1;
        vector<vector<int>> adj1(n),adj2(m);
        for(auto it:edges1){
            adj1[it[0]].push_back(it[1]);
            adj1[it[1]].push_back(it[0]);
        }
        for(auto it:edges2){
            adj2[it[0]].push_back(it[1]);
            adj2[it[1]].push_back(it[0]);
        }
        vector<bool> vis(n,0),vis2(n,0);
        auto f1 = dfs(0,vis,adj1);
        auto ans1 = dfs(f1.first,vis2,adj1);

        vector<bool> vis3(m,0),vis4(m,0);
        auto f2 = dfs(0,vis3,adj2);
        auto ans2 = dfs(f2.first,vis4,adj2);
        //cout<<ans1.second <<" "<<ans2.second<<endl;
        return max((ans1.second/2)+(ans2.second/2)+1,max(ans1.second-1,ans2.second-1));
    }
};
