class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int diameter1 = calculateDiameter(edges1);
        int diameter2 = calculateDiameter(edges2);
        return max((diameter1 + 1) / 2 + (diameter2 + 1) / 2 + 1, max(diameter1, diameter2));
    }

private:
    int calculateDiameter(const vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<vector<int>> adj(n);
        for (const auto& edge : edges) {
            adj[edge[0]].push_back(edge[1]);
            adj[edge[1]].push_back(edge[0]);
        }
        int farthest = findFarthestNode(0, adj);
        pair<int, int> farthestFromFarthest = bfs(farthest, adj);
        return farthestFromFarthest.second;
    }

    int findFarthestNode(int start, const vector<vector<int>>& adj) {
        return bfs(start, adj).first;
    }

    pair<int, int> bfs(int start, const vector<vector<int>>& adj) {
        int n = adj.size();
        vector<int> dist(n, -1);
        queue<int> q;
        q.push(start);
        dist[start] = 0;
        int farthestNode = start;
        int maxDist = 0;

        while (!q.empty()) {
            int node = q.front();
            q.pop();
            for (int neighbor : adj[node]) {
                if (dist[neighbor] == -1) {
                    dist[neighbor] = dist[node] + 1;
                    q.push(neighbor);
                    if (dist[neighbor] > maxDist) {
                        maxDist = dist[neighbor];
                        farthestNode = neighbor;
                    }
                }
            }
        }
        return {farthestNode, maxDist};
    }
};
