#include <bits/stdc++.h>
using namespace std;
using ll = long long;
using vi = vector<int>;
#define pb push_back
#define rsz resize
#define all(x) begin(x), end(x)
#define sz(x) (int)(x).size()
using pi = pair<int, int>;
#define f first
#define s second

class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size() + 1, m = edges2.size() + 1;
        vector<vector<int>> adj(n), adj2(m);
        for (auto& edge: edges1) {
            adj[edge[0]].pb(edge[1]);
            adj[edge[1]].pb(edge[0]);
        }
        for (auto& edge: edges2) {
            adj2[edge[0]].pb(edge[1]);
            adj2[edge[1]].pb(edge[0]);
        }
        int ret = 0;
          function<int(int, int, vector<vector<int>>&)> dfs = [&](int node, int par, vector<vector<int>>& ad) {                  
            int ans = 0;
            vector<int> temp;
            for (auto nei : ad[node]) {
              if (nei != par) {
                int t = dfs(nei, node, ad);
                temp.pb(t);
                ans = max(ans, t);
              }
            }
            sort(temp.rbegin(), temp.rend());
            if (temp.size() >= 2)
              ret = max(ret, temp[0] + temp[1] + 1);
            else if (temp.size() == 1)
              ret = max(ret, temp[0] + 1);
            else 
              ret = max(ret, 1);
            return ans + 1;
          };
        int ans = 0;
        if (n > 1) {
            ret = 0;
            dfs(0, -1, adj);
            ans = max(ans, ret - 1);
        }
        int ans2 = 0;
        if (m > 1) {
            ret = 0;
            dfs(0, -1, adj2);
            ans2 = max(0, ret - 1);
        }
        // cout << n << ' ' << m << ' ' << endl;
        // cout << ans << ' ' << ans2 << ' ' << endl;
        return max({(ans + 1) / 2 + 1 + (ans2 + 1) / 2, ans, ans2});
    }
};
