class Solution {
public:
    void dfs(int node,vector<int>&vis,vector<vector<int>>&adj,vector<int>&dist){
        vis[node]=1;
        int ans=0;
        for(auto it:adj[node]){
            if(vis[it]) continue;
            dist[it]=dist[node]+1;
            dfs(it,vis,adj,dist);
        }
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1,m=edges2.size()+1;
        int dia1=0,dia2=0;
        if(n>1){
            vector<vector<int>>adj(n);
            for(int i=0;i<n-1;i++){
                adj[edges1[i][0]].push_back(edges1[i][1]);
                adj[edges1[i][1]].push_back(edges1[i][0]);
            }
            vector<int>vis(n,0),dist(n,0);
            dfs(0,vis,adj,dist);
            int x=0,ind=-1;
            for(int i=0;i<n;i++){
                if(dist[i]>x){
                    x=dist[i];
                    ind=i;
                }
            }
            dist.assign(n,0);
            vis.assign(n,0);
            dfs(ind,vis,adj,dist);
            for(int i=0;i<n;i++){
                dia1=max(dia1,dist[i]);
            }
        }
        if(m>1){
            vector<vector<int>>adj(m);
            vector<int>vis(m,0),dist(m,0);
            for(int i=0;i<m-1;i++){
                adj[edges2[i][0]].push_back(edges2[i][1]);
                adj[edges2[i][1]].push_back(edges2[i][0]);
            }
            dfs(0,vis,adj,dist);
            int x=0,ind=-1;
            for(int i=0;i<m;i++){
                if(dist[i]>x){
                    x=dist[i];
                    ind=i;
                }
            }
            dist.assign(m,0);
            vis.assign(m,0);
            dfs(ind,vis,adj,dist);
            for(int i=0;i<m;i++){
                dia2=max(dia2,dist[i]);
            }
        }
        // if(dia1==0&&dia2==0) return 0;
        return max({dia1,dia2,(dia1+1)/2+(dia2+1)/2+1});
    }
};
