class Solution {
public:

    void dfs(int i, int par, vector<int>g[], vector<int>&height){

        for(auto k : g[i]){
            if(k==par)continue;
            dfs(k,i,g,height);
            height[i] = max(height[i],height[k]);
        }
        height[i]+=1;
    }

    void dfs2(int cur, int par, vector<int>g[],vector<int>&height,vector<int>&dist){

        int max1 = 0;
        int max2 = 0;

        for (auto u : g[cur]) {
            if (u != par) {
                if (height[u] >= max1) {
                    max2 = max1;
                    max1 = height[u];
                }
                else if (height[u] > max2) {
                    max2 = height[u];
                }
            }
        }
        for (auto u : g[cur]) {
            if (u != par) {
                if (max1 == height[u])
                    dist[u] = 1 + max(1 + max2, dist[cur]);
                else
                    dist[u] = 1 + max(1 + max1, dist[cur]);
                dfs2(u, cur,g,height,dist);
            }
        }
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size()+1;
        int m = edges2.size()+1;
        vector<int>g1[n+1],g2[m+1];
        for(auto k :  edges1){
            g1[k[0]].push_back(k[1]);
            g1[k[1]].push_back(k[0]);
        }
        for(auto k :  edges2){
            g2[k[0]].push_back(k[1]);
            g2[k[1]].push_back(k[0]);
        }
        vector<int>h1(n,0),h2(m,0),d1(n,0),d2(m,0);
        dfs(0,-1,g1,h1);
        dfs(0,-1,g2,h2);
        dfs2(0, -1,g1,h1,d1);
        dfs2(0, -1,g2,h2,d2);

        int a=n,b=m,dia1=0,dia2=0;
        for(int i=0;i<n;i++){h1[i] = max(h1[i],d1[i])-1;dia1 = max(dia1,h1[i]); a=min(a,h1[i]);}
        for(int i=0;i<m;i++){h2[i] = max(h2[i],d2[i])-1;dia2 = max(dia2,h2[i]); b=min(b,h2[i]);}
        dia1 = max(dia1,dia2);
        return max(dia1, a+b+1);
    }



};
