class Solution {
     ArrayList<Integer>[] adj;
     final int max = 100005;
     int[] first;
     int[] second;
     int[] c;
    public  void dfs(int v, int p) {
		first[v] = 0;
		second[v] = 0;
		for (int x : adj[v]) {
			if (x == p) continue;
			dfs(x, v);
			if (first[x] + 1 > first[v]) {
				second[v] = first[v];
				first[v] = first[x] + 1;
				c[v] = x;
			} else if (first[x] + 1 > second[v]) {
				second[v] = first[x] + 1;
			}
		}
	}
    public  void dfs2(int v, int p) {
		for (int x : adj[v]) {
			if (x == p) continue;
			if (c[v] == x) {
				if (first[x] < second[v] + 1) {
					second[x] = first[x];
					first[x] = second[v] + 1;
					c[x] = v;
				} else {
					second[x] = Math.max(second[x], second[v] + 1);
				}
			} else {
				second[x] = first[x];
				first[x] = first[v] + 1;
				c[x] = v;
			}
			dfs2(x, v);
		}
	}

    int[] minimumDiameter(int[][] edge){
        int n = edge.length;
        adj = new ArrayList[n + 1];
	    first = new int[max];  
	    second = new int[max];
        c =new int[max]; 
        for(int i=0;i<=n;i++)adj[i] = new ArrayList<>();
        for (int[] i:edge) {
			adj[i[0]].add(i[1]);
			adj[i[1]].add(i[0]);
		}
        dfs(0, -1);
		dfs2(0, -1);
        int ans = max;
        int max_val = 0;
        //System.out.println(Arrays.toString(Arrays.copyOf(first,n+1)));
        for(int i=0;i<=n;i++){
            ans = Math.min(ans,first[i]);
            max_val = Math.max(max_val,first[i]);
        }
        return new int[]{ans,max_val};
    }
    public int minimumDiameterAfterMerge(int[][] edges1, int[][] edges2) {
        int[] first = minimumDiameter(edges1),second = minimumDiameter(edges2);
        //System.out.println("===");
        return Math.max(first[0]+second[0]+1,Math.max(first[1],second[1]));
    }
}
