class Solution {
public:

    void dfs(int node,int par,vector<int>adj[],vector<int>&h){

        //int sum=0;
        for(auto it:adj[node]){
            if(it==par)continue;
            //sum=max(sum,dfs(it,node,adj,h));
            h[it]=h[node]+1;
            dfs(it,node,adj,h);
        }
        return;
    }
    vector<int>func(int n,vector<int>adj[]){

        vector<int>h(n,0);
        dfs(0,-1,adj,h);
             //  for(auto it:h)cout<<it<<" "; cout<<endl;

        int node=0,maxi=0;
        for(int i=0;i<n;i++){
            if(maxi<h[i]){
                maxi=h[i];
                node=i;
            }
            h[i]=0;
        }

        dfs(node,-1,adj,h);
         // cout<<n<<" "<<node<<"=>";  for(auto it:h)cout<<it<<" "; cout<<endl;

         node=0;
         maxi=0;
        for(int i=0;i<n;i++){
            if(maxi<h[i]){
                maxi=h[i];
                node=i;
            }
        }

        vector<int>h1(n,0);
        dfs(node,-1,adj,h1);
         // cout<<n<<" "<<node<<"=>";  for(auto it:h)cout<<it<<" "; cout<<endl;

        for(int i=0;i<n;i++)h[i]=max(h[i],h1[i]);
        return h; 
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {

        int n=edges1.size()+1;
        int m=edges2.size()+1;

        vector<int>adj1[n];
        vector<int>adj2[m];

        for(auto it:edges1){
            adj1[it[0]].push_back(it[1]);
            adj1[it[1]].push_back(it[0]);
        }
         for(auto it:edges2){
            adj2[it[0]].push_back(it[1]);
            adj2[it[1]].push_back(it[0]);
        }

        int d1=0,d2=0;
        vector<int>h1=func(n,adj1);
        vector<int>h2=func(m,adj2);


        int mini1=1e9,mini2=1e9;
        for(int i=0;i<n;i++){
            d1=max(d1,h1[i]);
            mini1=min(mini1,h1[i]);
        }
        for(int i=0;i<m;i++){
            d2=max(d2,h2[i]);
            mini2=min(mini2,h2[i]);
        }


       // for(auto it:h1)cout<<it<<" "; cout<<endl;
       // for(auto it:h2)cout<<it<<" "; cout<<endl;
        int ans=0;
        ans=max(d1,d2);

        ans=max(ans,mini1+mini2+1);



        return ans;
    }
};
