class Solution {
public:
    void dfs(vector<int> adj[], int i, int sz[], int p){
        for(int j=0;j<adj[i].size();j++){
            if(p==adj[i][j]){
                continue;
            }
            sz[adj[i][j]]=1+sz[i];
            dfs(adj,adj[i][j],sz,i);
        }
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int aa=0,bb=0;
        int n=edges1.size()+1;
        int m=edges2.size()+1;
        vector<int> adj1[n];
        vector<int> adj2[m];
        int sz1[n],sz2[m];
        sz1[0]=0;sz2[0]=0;
        for(int i=0;i<n-1;i++){
            adj1[edges1[i][0]].push_back(edges1[i][1]);
            adj1[edges1[i][1]].push_back(edges1[i][0]);
        }
        for(int i=0;i<m-1;i++){
            adj2[edges2[i][0]].push_back(edges2[i][1]);
            adj2[edges2[i][1]].push_back(edges2[i][0]);
        }
        dfs(adj1,0,sz1,-1);
        dfs(adj2,0,sz2,-1);
        int c=0,d=0;
        for(int i=0;i<n;i++){
            if(sz1[c]<sz1[i]){
                c=i;
            }
        }
        for(int i=0;i<m;i++){
            if(sz2[d]<sz2[i]){
                d=i;
            }
        }
        sz1[c]=0;sz2[d]=0;
        // cout<<c<<" "<<d<<endl;
        dfs(adj1,c,sz1,-1);
        dfs(adj2,d,sz2,-1);
        for(int i=0;i<n;i++){
           aa=max(aa,sz1[i]);
        }
        for(int i=0;i<m;i++){
           bb=max(bb,sz2[i]);
        }
        return max((aa+1)/2+1+(bb+1)/2,max(aa,bb));
    }
};
