func minimumDiameterAfterMerge(edges1 [][]int, edges2 [][]int) int {
	d := func(e [][]int) int {
		n := len(e) + 1
		a := make([][]int, n)
		for _, p := range e {
			a[p[0]] = append(a[p[0]], p[1])
			a[p[1]] = append(a[p[1]], p[0])
		}

		b := func(s int) (int, int) {
			d := make([]int, n)
			for i := range d {
				d[i] = -1
			}
			q := list.New()
			q.PushBack(s)
			d[s] = 0
			f := s

			for q.Len() > 0 {
				v := q.Remove(q.Front()).(int)
				for _, u := range a[v] {
					if d[u] == -1 {
						d[u] = d[v] + 1
						q.PushBack(u)
						if d[u] > d[f] {
							f = u
						}
					}
				}
			}
			return f, d[f]
		}

		u, _ := b(0)
		_, dia := b(u)
		return dia
	}

	d1 := d(edges1)
	d2 := d(edges2)

	return m(d1, d2, (d1+1)/2+(d2+1)/2+1)
}
