class Solution {    
    public int minimumDiameterAfterMerge(int[][] edges1, int[][] edges2) {
        return Math.max(Math.max(gao1(edges1), gao1(edges2)), gao(edges1) + gao(edges2) + 1);
    }
    private int gao(int[][] edges) {
        if (edges.length == 0) return 0;
        int n = edges.length + 1;
        int[] ind = new int[n];
        List<Integer>[] sons = new ArrayList[n];
        Arrays.setAll(sons, a->new ArrayList<>());
        Deque<Integer> deque = new ArrayDeque<>();
        for (int[] edge : edges) {
            int x = edge[0], y = edge[1];
            sons[x].add(y); sons[y].add(x);
            ind[x]++; ind[y]++;
        }
        for (int i = 0; i < n; i++) {
            if (ind[i] <= 1) deque.add(i);
        }
        int re = 0;
        while (!deque.isEmpty()) {
            int size = deque.size();
            if (size == 1) break;
            while (size-- > 0) {
                int p = deque.pollFirst();
                for (int next : sons[p]) {
                    if (--ind[next] == 1) deque.addLast(next);
                }
            }
            re++;
        }
        return re;
    }

    int gao1(int[][] edges) {
        if (edges.length == 0) return 0;
        int n = edges.length + 1;
        int[] ind = new int[n];
        List<Integer>[] sons = new ArrayList[n];
        Arrays.setAll(sons, a->new ArrayList<>());
        for (int[] edge : edges) {
            int x = edge[0], y = edge[1];
            sons[x].add(y); sons[y].add(x);
            ind[x]++; ind[y]++;
        }
        md = mi = 0;
        dfs(0, -1,0, sons);
        dfs(mi, -1, 0, sons);
        return md;
    }
    int md, mi;
    void dfs(int cur, int pre, int deep, List<Integer>[] sons) {
        if (deep > md) {
            md = deep;
            mi = cur;
        }
        for (int next : sons[cur]) {
            if (next == pre) continue;
            dfs(next, cur, deep+1, sons);
        }
    }
}
