#define pb push_back;
class Solution {
public:
    int res=0;
    int val=0;
    int dfs(int node,vector<vector<int>> &adj,int par=-1){
        int v1=0,v=0;
        for(auto child:adj[node]){
            if(child!=par)
            {
                int temp=dfs(child,adj,node);

                if(temp>v1){
                    v=v1;
                    v1=temp;
                    continue;
                }
                if(temp>v){
                    v=temp;
                }
            }
        }
        res=max(res,1+v1+v);
        val=max(val,1+v+v1);
        int x=v1+1;
        return x;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& g1, vector<vector<int>>& g2) {
        res=0;
        val=0;
        vector<vector<int>> adj1(g1.size()+1),adj2(g2.size()+1);
        for(int i=0;i<g1.size();i++)
        {
            adj1[g1[i][0]].push_back(g1[i][1]);
            adj1[g1[i][1]].push_back(g1[i][0]);
        }
        for(int i=0;i<g2.size();i++)
        {
            adj2[g2[i][0]].push_back(g2[i][1]);
            adj2[g2[i][1]].push_back(g2[i][0]);
        }
        int ans=0;
        dfs(0,adj1);
        ans+=val/2;
        ans++;
        val=0;

        dfs(0,adj2);
        ans+=val/2;
        ans++;
        res = max(res,ans);
        res--;
        return res;
    }
};
