class Solution {
public:
    int dfs(int v,int p,vector<int>g[],int &ans){
        int dia=1,maxi1=0,maxi2=0;
        //cout<<v<<" "<<p<<endl;
        for(auto c:g[v]){
            if(c!=p){
                int len=dfs(c,v,g,ans);
                if(maxi1<len){
                    maxi2=maxi1;
                    maxi1=len;
                } else if( maxi2<len) maxi2=len;
            }
        }
        //cout<<maxi1<<" "<<maxi2<<" "<<ans<<endl;
        ans=max(maxi1+maxi2+dia,ans);

        return maxi1+dia;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1,m=edges2.size()+1;
        vector<int>g1[n],g2[m];

        for(auto e:edges1){
            g1[e[0]].push_back(e[1]);
            g1[e[1]].push_back(e[0]);
        }

        for(auto e:edges2){
            g2[e[0]].push_back(e[1]);
            g2[e[1]].push_back(e[0]);
        }

        int d1=0,d2=0;
        dfs(0,-1,g1,d1);
        dfs(0,-1,g2,d2);

        d1--;d2--;
        //cout<<d1<<" "<<d2<<endl;

        return max({d1,d2,1+(d1+1)/2+(d2+1)/2});
    }
};
