class Solution {
    public function minimumDiameterAfterMerge($edges1, $edges2) {
        $n = count($edges1) + 1;
        $m = count($edges2) + 1;

        $adj1 = array_fill(0, $n, []);
        $adj2 = array_fill(0, $m, []);

        foreach ($edges1 as $edge) {
            $adj1[$edge[0]][] = $edge[1];
            $adj1[$edge[1]][] = $edge[0];
        }
        foreach ($edges2 as $edge) {
            $adj2[$edge[0]][] = $edge[1];
            $adj2[$edge[1]][] = $edge[0];
        }

        $dfs = function($adj, $start, &$farthestNode) {
            $n = count($adj);
            $dist = array_fill(0, $n, -1);
            $dfsVisit = function($node, $d) use (&$dist, &$farthestNode, &$adj, &$dfsVisit) {
                $dist[$node] = $d;
                if ($d > $dist[$farthestNode]) {
                    $farthestNode = $node;
                }
                foreach ($adj[$node] as $neighbor) {
                    if ($dist[$neighbor] == -1) {
                        $dfsVisit($neighbor, $d + 1);
                    }
                }
            };
            $farthestNode = $start;
            $dfsVisit($start, 0);
            return $dist[$farthestNode];
        };

        $farthestNode1 = 0;
        $dfs($adj1, 0, $farthestNode1);
        $diameter1 = $dfs($adj1, $farthestNode1, $farthestNode1);

        $farthestNode2 = 0;
        $dfs($adj2, 0, $farthestNode2);
        $diameter2 = $dfs($adj2, $farthestNode2, $farthestNode2);

        $minDiameter = max($diameter1, $diameter2);
        $half1 = intdiv($diameter1 + 1, 2);
        $half2 = intdiv($diameter2 + 1, 2);
        $minDiameter = max($minDiameter, $half1 + $half2 + 1);

        return $minDiameter;
    }
}

// Example usage:
$solution = new Solution();
$edges1 = [[0, 1], [1, 2], [1, 3]];
$edges2 = [[0, 1], [1, 2], [2, 3], [3, 4]];
echo "Minimum diameter after merge: " . $solution->minimumDiameterAfterMerge($edges1, $edges2) . "\n";
