class Solution {
public:



    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1;
        int m=edges2.size()+1;
    vector<vector<int>>adj(n);
        vector<vector<int>>adj2(m);
        for(auto c:edges1){
            adj[c[0]].push_back(c[1]);
            adj[c[1]].push_back(c[0]);
        }
        for(auto c:edges2){
            adj2[c[0]].push_back(c[1]);
            adj2[c[1]].push_back(c[0]);
        }
        int d1,d2;
        if(n==1){
              d1=0;
        }
        else{
        int dia=0,dia2;
            vector<int>vis(n,0);
            vector<int>ds(n,0);
            queue<int>q;
q.push(dia);

vis[dia]++;
while(q.size()){
	int tp=q.front();
	q.pop();
	for(auto c:adj[tp]){
        if(!vis[c]){
        	dia2=c;
        	q.push(c);
        	vis[c]++;
        }
	}
}
            q.push(dia2);
vector<int> vis2(n,0);
vis2[dia2]++;
while(q.size()){
	int tp=q.front();
	q.pop();
	for(auto c:adj[tp]){
        if(!vis2[c]){
        	 ds[c]=ds[tp]+1;
            d1=ds[c];
        	q.push(c);
        	vis2[c]++;
        }
	}
}



        }


if(m==1){
  d2=0;
}
else{
  int dia=0,dia2;
            vector<int>vis(m,0);
            vector<int>ds(m,0);
            queue<int>q;
q.push(dia);

vis[dia]++;
while(q.size()){
	int tp=q.front();
	q.pop();
	for(auto c:adj2[tp]){
        if(!vis[c]){
        	dia2=c;
        	q.push(c);
        	vis[c]++;
        }
	}
}
            q.push(dia2);
vector<int> vis2(m,0);
vis2[dia2]++;
while(q.size()){
	int tp=q.front();
	q.pop();
	for(auto c:adj2[tp]){
        if(!vis2[c]){
        	 ds[c]=ds[tp]+1;
            d2=ds[c];
        	q.push(c);
        	vis2[c]++;
        }
	}
}


}

        int x=(d1+1)/2;
        int y=(d2+1)/2;
        return max({x+y+1,d1,d2});




    }
