int dis[100005];
vector<int> v[100005];
class Solution {
public:
void dfs(int x,int par)
{
    for(int y : v[x])
    {
        if(y != par)
        {
            dis[y] = dis[x] + 1;
            dfs(y,x);
        }
    }
}
int get(vector< vector<int> > a)
{
    int n = a.size() + 1;

    int i;
    int x,y;
    for(i=0;i<=n;i++)
    v[i].clear();

    for(i=0; i<n-1; i++)
    {
        x = a[i][0]+1;
        y = a[i][1]+1;
        v[x].push_back(y);
        v[y].push_back(x);
    }

    for(i=0;i<=n;i++)dis[i] = 0;

    dfs(1,0);

    int ma = 0;
    for(i=1; i<=n; i++)
    if(dis[i] > dis[ma])ma = i;



    for(i=1; i<=n; i++)dis[i] = 0;

     dfs(ma,0);

     ma = 1;
    for(i=1;i<=n;i++)
    if(dis[i] > dis[ma])ma = i;

    return dis[ma];
}
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int fir = get(edges1);
        int sec = get(edges2);
        printf("%d %d\n",fir,sec);
        int cur = max(fir,sec);
        int res =  1 + (fir+1)/2 + (sec+1)/2;
        return max(cur,res);
    }
};
