class Solution {
public:
    int ghei(int i, int par, vector<int> &hei, vector<vector<int>> &tree){
        int h=0;
        for(auto nei: tree[i]){
            if(nei==par) continue;
            h=max(h, 1+ghei(nei, i, hei, tree));
        }

        return hei[i]=h;
    }

    void cal(int curr, int pc, int par, int &maxi, int &mhei, vector<int> &hei, vector<vector<int>> &tree){
        int fhei=INT_MIN, shei=INT_MIN, fchi=-1;
        for(auto nei: tree[curr]){
            if(nei==par) continue;
            if(hei[nei]>=fhei){
                shei=fhei;
                fhei=hei[nei];
                fchi=nei;
            }
            else if(hei[nei]>=shei){
                shei=hei[nei];
            }
        }

        int chei=max(fhei, pc)+1;
        maxi=max(maxi, chei);
        mhei=min(mhei, chei);

        for(auto nei: tree[curr]){
            if(nei==par) continue;
            if(nei==fchi) cal(nei, max(pc, shei)+1, curr, maxi, mhei, hei, tree);
            else cal(nei, max(pc, fhei)+1, curr, maxi, mhei, hei, tree);
        }
    }

    void createTree(vector<vector<int>> &tree, vector<vector<int>> &edges){
        for(auto &e: edges){
            tree[e[0]].push_back(e[1]);
            tree[e[1]].push_back(e[0]);
        }
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1, m=edges2.size()+1;
        vector<vector<int>> a(n), b(m);
        vector<int> ahei(n, 0), bhei(m, 0);
        int aminhei=INT_MAX, amaxi=INT_MIN, bminhei=INT_MAX, bmaxi=INT_MIN;

        createTree(a, edges1); createTree(b, edges2);
        ghei(0, -1, ahei, a); ghei(0, -1, bhei, b);
        cal(0, -1, -1, amaxi, aminhei, ahei, a); 
        cal(0, -1, -1, bmaxi, bminhei, bhei, b);

        int maxi=max(amaxi, bmaxi);

        int ans=aminhei+bminhei+1;
        return ans>maxi?ans:maxi;

    }
};
