#define MAXN 3000001
int spf[MAXN];
int larg[MAXN];
int primes[MAXN];
int cnt[MAXN];
void sieve()
{
    spf[1] = 1;
    for (int i = 2; i < MAXN; i++){
        spf[i] = i;
    }
    for (int i = 4; i < MAXN; i += 2)
        spf[i] = 2;
    for (int i = 3; i * i < MAXN; i++) {
        if (spf[i] == i) {
            for (int j = i * i; j < MAXN; j += i)
                if (spf[j] == j)
                    spf[j] = i;
        }
    }
    int cur=0;
    for(int i=2;i<MAXN;++i){
        if(spf[i]==i){
            primes[i]=++cur;
        }
        else{
            larg[i]=i/spf[i];
        }
    }
}
class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
      function<int(int,int,vi&,vvi&)>helper=[&](int src,int n,vi& dist,vvi& adj)->int{
        queue<int>q;
          vi vis(n,0);
        q.push(src);
        dist[src]=0;
        vis[src]=1;

        while(!q.empty()){
          int x=q.front();
            q.pop();
          for(auto e:adj[x]){
            if(!vis[e]){
              dist[e]=dist[x]+1;
                q.push(e);
              vis[e]=1;
            }
          }
        }
        return (max_element(all(dist))-dist.begin());
      };
      int n=edges1.size()+1,m=edges2.size()+1;
      vvi adj1(n),adj2(m);
      for(auto e:edges1){
        adj1[e[0]].pb(e[1]);
        adj1[e[1]].pb(e[0]);
      }
      for(auto e:edges2){
        adj2[e[0]].pb(e[1]);
        adj2[e[1]].pb(e[0]);
      }
      auto helper2=[&](vvi &adj,int n)->pii{
        vi dist1(n,0);
        int dia1=helper(0,n,dist1,adj);
        dist1.assign(n,0);
        int dia2=helper(dia1,n,dist1,adj);
        vi dist2(n,0);
        helper(dia2,n,dist2,adj);
        int mn=INFI,mx=0;
        for(int i=0;i<n;++i){
          mn=min({mn,max(dist1[i],dist2[i])});
          mx=max({mx,dist1[i],dist2[i]});
        } 
        return {mn,mx};
      };
      auto [a,b]=helper2(adj1,n);
      auto [c,d]=helper2(adj2,m);
      return max({b,d,a+c+1});
    }
};
