class Solution {
public:
    pair<int, int> bfs(const vector<vector<int>>& graph, int start) {
        int n = graph.size();
        vector<int> dist(n, -1);
        queue<int> q;
        q.push(start);
        dist[start] = 0;
        int farthest_node = start;
        int max_dist = 0;

        while (!q.empty()) {
            int node = q.front();
            q.pop();
            for (int neighbor : graph[node]) {
                if (dist[neighbor] == -1) {
                    dist[neighbor] = dist[node] + 1;
                    q.push(neighbor);
                    if (dist[neighbor] > max_dist) {
                        max_dist = dist[neighbor];
                        farthest_node = neighbor;
                    }
                }
            }
        }

        return {farthest_node, max_dist};
    }

    int find_diameter(const vector<vector<int>>& graph) {
        pair<int, int> p1 = bfs(graph, 0);
        pair<int, int> p2 = bfs(graph, p1.first);
        return p2.second;
    }

    vector<int> find_centroids(const vector<vector<int>>& graph) {
        int n = graph.size();
        vector<int> degree(n, 0);
        vector<int> leaves;

        for (int i = 0; i < n; ++i) {
            degree[i] = graph[i].size();
            if (degree[i] == 1 || degree[i] == 0) {
                leaves.push_back(i);
            }
        }

        int count = leaves.size();
        while (count < n) {
            vector<int> new_leaves;
            for (int leaf : leaves) {
                for (int neighbor : graph[leaf]) {
                    if (--degree[neighbor] == 1) {
                        new_leaves.push_back(neighbor);
                    }
                }
            }
            count += new_leaves.size();
            leaves = new_leaves;
        }

        return leaves;
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size() + 1;
        int m = edges2.size() + 1;

        vector<vector<int>> tree1(n);
        vector<vector<int>> tree2(m);

        for (const auto& edge : edges1) {
            tree1[edge[0]].push_back(edge[1]);
            tree1[edge[1]].push_back(edge[0]);
        }

        for (const auto& edge : edges2) {
            tree2[edge[0]].push_back(edge[1]);
            tree2[edge[1]].push_back(edge[0]);
        }

        int diameter1 = find_diameter(tree1);
        int diameter2 = find_diameter(tree2);

        vector<int> centroids1 = find_centroids(tree1);
        vector<int> centroids2 = find_centroids(tree2);

        int min_diameter = INT_MAX;
        for (int c1 : centroids1) {
            for (int c2 : centroids2) {
                int new_diameter = max({(diameter1 + 1) / 2 + (diameter2 + 1) / 2 + 1, diameter1, diameter2});
                min_diameter = min(min_diameter, new_diameter);
            }
        }

        return min_diameter;
    }
};
