class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& e1, vector<vector<int>>& e2) {
        n = e1.size()+1;
        rep(i,1,n)v[i].clear();
        for(auto &i:e1){
            a = i[0]+1;
            b = i[1]+1;
            v[a].pb(b);
            v[b].pb(a);
        }
        mx = 1;
        vis[mx] = 0;
        dfs(mx,0);
        rep(i,1,n)if(vis[i]>vis[mx])mx = i;
        vis[mx] = 0;
        dfs(mx,0);
        rep(i,1,n)if(vis[i]>vis[mx])mx = i;
        x = vis[mx];
        swap(e1, e2);

        n = e1.size()+1;
        rep(i,1,n)v[i].clear();
        for(auto &i:e1){
            a = i[0]+1;
            b = i[1]+1;
            v[a].pb(b);
            v[b].pb(a);
        }
        mx = 1;
        vis[mx] = 0;
        dfs(mx,0);
        rep(i,1,n)if(vis[i]>vis[mx])mx = i;
        vis[mx] = 0;
        dfs(mx,0);
        rep(i,1,n)if(vis[i]>vis[mx])mx = i;
        y = vis[mx];
        return max({x,y,(x+1)/2+(y+1)/2+1});
    }
};
