class Solution {
public:

    int get(vector<vector<int>> &e)
    {
        if(e.size()==0)
            return 0 ;
        int n = e.size()+2 ; 
        vector<vector<int>> g(n);
        for(auto & i : e)
            g[i[0]].push_back(i[1]),  g[i[1]].push_back(i[0]); 


        int leaf = -1, dis = 0 ; 
        queue<pair<int,int>> q; 
        vector<int> vis(n+1,0) ; 
        q.push({0,0}); 
        while(!q.empty())
        {
            pair<int,int> p = q.front();
            q.pop(); 
            int node = p.first ; 
            int diss = p.second ; 
            vis[node] = 1 ; 
            if(diss > dis)
            {
                dis = diss , leaf = node ; 
            }

            for(auto & i : g[node])
            {
                if(!vis[i])
                    q.push({i,diss+1}) ; 
            }
        }


        int mx = 0 ; 


        q.push({leaf,0});
        vis.clear();
        vis.resize(n+1,0); 
        while(!q.empty())
        {
            pair<int,int> p =q.front();
            q.pop();

            int node = p.first ; 
            int diss = p.second ; 
            mx = max(mx,diss) ; 
              vis[node] = 1 ; 

            for(auto & i : g[node])
            {
                if(!vis[i])
                {
                    q.push({i,diss+1}); 
                }
            }

        }
        return mx ; 
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int a = get(edges1);
        int b = get(edges2);




        a++ , b++ ;
         if(edges1.size()==0 and edges2.size()==0)
            return 1 ; 



        int p1 = a/2 + b/2 + 2 ; 

        p1-- ; 
        // cout<<"a : "<<a<<" b : "<<b<<"\n" ; 
        int fans = 0 ;
        fans = max({p1 , a-1 , b-1}) ; 
        return fans ; 
    }
};
