// Containers
typedef unsigned int uint;
typedef long long ll;
typedef unsigned long long ull;
typedef pair<int, int>	    pii;
typedef pair<ll, ll>	    pll;
typedef vector<int>		    vi;
typedef vector<ll>		    vl;
typedef vector<pii>		    vpii;
typedef vector<pll>		    vpll;
typedef vector<vi>		    vvi;
typedef vector<vl>		    vvl;
typedef pair<string,string> pss;
typedef map<int, int>	    mii;

// Miscellanous
#define pb push_back
#define ff first
#define ss second
#define sz(v) (int)v.size()
#define all(v) v.begin(),v.end()
#define sortv(v) sort(all(v))
template <class T>
void clr(T* arr, int size) {memset(arr, 0, size * sizeof(T));}

void pnl() {printf("\n");}
void pyes() {printf("YES\n");}
void pno() {printf("NO\n");}

// Loops
#define loop(i,s,e) for (int (i)=(s);(i)<(e);++(i))
#define loope(i,s,e) for (int (i)=(s);(i)<=(e);++(i))
#define forc(c,s,e) for (char (c)=(s);(c)<=(e);++(c))
#define rep(i,n) loop(i,0,n)
#define repn(i,n) loope(i,1,n)

const int MAXN = 1e5;
vi adj[MAXN+1];

class Solution {
public:
    int n;

    vi bfs(int src)
    {
        vi d(n+1, -1);
        queue<int> q;

        d[src] = 0;
        q.push(src);
        while(!q.empty())
        {
            int node = q.front();
            q.pop();

            for(auto nbr : adj[node]) if(d[nbr] == -1) d[nbr] = d[node] + 1, q.push(nbr);
        }
        return d;
    }

    int solve()
    {
        vi v1 = bfs(1);
        int p1 = max_element(all(v1)) - v1.begin();

        vi v2 = bfs(p1);

        return (*max_element(all(v2))) + 1;
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& e1, vector<vector<int>>& e2) {
        n = sz(e1) + 1;

        repn(i,n) adj[i].clear();
        rep(i,n-1) 
        {
            int u = e1[i][0];
            int v = e1[i][1];
            u++ , v++;
            adj[u].pb(v);
            adj[v].pb(u);
        }
        int n1 = solve();

        n = sz(e2) + 1;
        repn(i,n) adj[i].clear();
        rep(i,n-1) 
        {
            int u = e2[i][0];
            int v = e2[i][1];
            u++ , v++;
            adj[u].pb(v);
            adj[v].pb(u);
        }

        int n2 = solve();

        return max({n1, n2, n1/2 + n2/2 + 2}) - 1;
    }
};
