class Solution
{
public:
    void dfs2(int node, int d,vll &dpth,vvll&gr,int &end)
    {
        dpth[node] = d;
        if (d > dpth[end])
        {
            end = node;
        }
        for (int it : gr[node])
        {
            if (dpth[it] == -1)
            {
                dfs2(it, d + 1,dpth,gr,end);
            }
        }
    };
int dfs(vvll &gr, int st, int &end)
{
    int n = sz(gr);
    vll dpth(n+10, -1);

    // end = 1;
    // for(int i=2; i<=n; i++){
    //     if(dpth[i] > dpth[end]){
    //         end = i;
    //     }
    // }
   // end = st;
    dfs2(st, 0,dpth,gr,end);
    return dpth[end];
};
   int minimumDiameterAfterMerge(vector<vector<int>> &edges1, vector<vector<int>> &edges2)
   {
      int n = sz(edges1)+ 1, m = sz(edges2) + 1;

      vvll gr1(n),gr2(m);

      for ( auto &it: edges1)
      {
         gr1[it[0]].pb(it[1]);
         gr1[it[1]].pb(it[0]);
      }
      for ( auto &it: edges2)
      {
         gr2[it[0]].pb(it[1]);
         gr2[it[1]].pb(it[0]);
      }

        int f1 = 0;
        dfs(gr1, 0, f1);

        int dm = dfs(gr1, f1, f1);

        f1= 0;
        dfs(gr2, 0, f1);
        int dm2= dfs(gr2, f1, f1);
       return max(max(dm, dm2), (dm + 1) / 2 + (dm2 + 1) / 2 + 1);
   }
};
