class Solution {
public:
    void check(int node,vector<int>adj[],int papa,vector<int>&sub){
        sub[node]=1;
        for(int i:adj[node]){
            if(i!=papa){
                check(i,adj,node,sub);
                sub[node]=max(sub[node],1+sub[i]);
            }
        }
        return;
    }
    void dfs(int node,int papa,int upar,vector<int>adj[],vector<int>&sub,vector<int>&dp){
        vector<int>idhar;
        for(int i:adj[node]){
            if(i!=papa){
                idhar.push_back(sub[i]);
            }
        }
        sort(idhar.begin(),idhar.end());
        dp[node]=sub[node]-1;
        dp[node]=max(dp[node],upar);
        for(int i:adj[node]){
            if(i!=papa){
                int next=idhar[idhar.size()-1];
                if(sub[i]==next){
                    next=0;
                    if(idhar.size()>1){
                        next=idhar[idhar.size()-2];
                    }

                }
                dfs(i,node,1+max(upar,next),adj,sub,dp);
            }
        }
        return;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size();
        int m=edges2.size();
        n++;
        m++;
     vector<int>adj1[n];
        vector<int>adj2[m];
        for(int i=0;i<edges1.size();i++){
            adj1[edges1[i][0]].push_back(edges1[i][1]);
            adj1[edges1[i][1]].push_back(edges1[i][0]);

        }
        for(int i=0;i<edges2.size();i++){
            adj2[edges2[i][0]].push_back(edges2[i][1]);
            adj2[edges2[i][1]].push_back(edges2[i][0]);

        }
        vector<int>sub1(n,0);
        vector<int>sub2(m,0);
        check(0,adj1,-1,sub1);
        check(0,adj2,-1,sub2);
        vector<int>dp1(n,0);
        vector<int>dp2(m,0);
        dfs(0,-1,0,adj1,sub1,dp1);
        dfs(0,-1,0,adj2,sub2,dp2);
        int minno=INT_MAX;
        for(int i=0;i<n;i++){
            minno=min(minno,dp1[i]);
        }
        int min2=INT_MAX;
        for(int i=0;i<m;i++){
            min2=min(min2,dp2[i]);
        }

        minno+=min2;
        minno++;
        for(int i=0;i<n;i++){
            minno=max(minno,dp1[i]);
        }
        for(int j=0;j<m;j++){
            minno=max(minno,dp2[j]);
        }
        return minno;
    }
};
