class Solution
{
public:
    int minimumDiameterAfterMerge(vector<vector<int>> &edges1, vector<vector<int>> &edges2)
    {
        int n = edges1.size() + 1;
        int m = edges2.size() + 1;

        vector<vector<int>> ad1(n);
        vector<vector<int>> ad2(m);

        auto it1 = edges1.begin();
        while (it1 != edges1.end())
        {
            const auto &edge = *it1;
            ad1[edge[0]].push_back(edge[1]);
            ad1[edge[1]].push_back(edge[0]);
            ++it1;
        }

        auto it2 = edges2.begin();
        while (it2 != edges2.end())
        {
            const auto &edge = *it2;
            ad2[edge[0]].push_back(edge[1]);
            ad2[edge[1]].push_back(edge[0]);
            ++it2;
        }

        auto dfs = [](const vector<vector<int>> &ad, int start, int &farthestNode)
        {
            int n = ad.size();
            vector<int> dist(n, -1);
            function<void(int, int)> dfsVisit = [&](int node, int d)
            {
                dist[node] = d;
                if (d > dist[farthestNode])
                {
                    farthestNode = node;
                }
                for (int neighbor : ad[node])
                {
                    if (dist[neighbor] == -1)
                    {
                        dfsVisit(neighbor, d + 1);
                    }
                }
            };
            farthestNode = start;
            dfsVisit(start, 0);
            return dist[farthestNode];
        };

        int farNode = 0;
        dfs(ad1, 0, farNode);
        int dia1 = dfs(ad1, farNode, farNode);

        int farthestNode2 = 0;
        dfs(ad2, 0, farthestNode2);
        int dia2 = dfs(ad2, farthestNode2, farthestNode2);

        int minDia = max(dia1, dia2);
        int half1 = (dia1 + 1) / 2;
        int half2 = (dia2 + 1) / 2;
        minDia = max(minDia, half1 + half2 + 1);

        return minDia;
    }
};
