ll expo(ll a, ll b, ll m) {ll res = 1; while (b > 0) {if (b & 1) res = (res * a) % m; a = (a * a) % m; b = b >> 1;} return res%m ;}
int nCr(int n , int r){
    if (n < r) return 0;
    if (r > n - r) r = n - r;
    ll ans = 1; ll i;
    for (i = 1; i <= r; i++){ans = (ans*(n - r + i));ans /= i;} return ans;
}
