class Solution {

public:
    int mnH(VV &g, int n){
        queue<int> q;
        int h = 0;
        FOR(i,n){
            if(g[i].size() == 1){
                q.push(i);
            }
        }
        V vis(n);
        while(!q.empty()){
            int sz= q.size();
            h++;
            while(sz--){
                int v = q.front();
                q.pop();
                vis[v] = 1;
                for(auto u: g[v]){
                    if(vis[u] == 0){
                        q.push(u);
                    }
                } 
            }
        }
        return max(h-1,0);
    }

    pair<int,int> bfs(int src,VV &adj, int SZ)
    {
        SZ++;
        int d = 0;
        queue<pair<int,int> > q;
        q.push({src, 0});
        vector<bool> vis(SZ, false);
        pair<int,int>  u;

        while(!q.empty()) {
            u = q.front();
            vis[u.first] = true;
            q.pop();
            for(int v: adj[u.first])
                if(!vis[v])
                q.push({v, u.second + 1});
        }
        return u;
    }

    int dia(VV &g, int n){
        pair<int,int>  end1 = bfs(1,g,n);
	    pair<int,int>  end2 = bfs(end1.first,g,n);
        return end2.second;
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& a, vector<vector<int>>& b) {
        int n = a.size()+1, m = b.size()+1;
        VV g(n+1),G(m+1);
        FOR(i,n-1){
            g[a[i][0]].pb(a[i][1]);
            g[a[i][1]].pb(a[i][0]);
        }
        FOR(i,m-1){
            G[b[i][0]].pb(b[i][1]);
            G[b[i][1]].pb(b[i][0]);
        }

        // cout<<dia(g,n)<<" "<<dia(G,m)<<endl;
        // cout<< mnH(g,n) <<" "<< mnH(G,m) <<endl;
        int ans = max(dia(g,n),dia(G,m));
        return max(ans, (dia(g,n)+1)/2 + (dia(G,m)+1)/2 + 1);

        // return 0;


    }
};
