class Solution {
public:
    int diameter(vector<vector<int>> &tree1){
        int n = tree1.size();
        vector<int> dist(n, 0);
        vector<int> parent(n, -1); // Initialize parent array with -1
        queue<int> q;
        q.push(0);
        while(!q.empty()){
            int node = q.front();
            q.pop();
            for(auto it : tree1[node]){
                if(parent[node] != it){
                    parent[it] = node;
                    dist[it] = dist[node] + 1;
                    q.push(it);
                }
            }
        }
        int maxDist = 0;
        int maxNode = 0;
        for(int i = 0; i < n; i++){
            if(dist[i] > maxDist){
                maxDist = dist[i];
                maxNode = i;
            }
        }
        dist = vector<int>(n, 0);
        fill(parent.begin(), parent.end(), -1); // Re-initialize parent array with -1
        q.push(maxNode);
        while(!q.empty()){
            int node = q.front();
            q.pop();
            for(auto it : tree1[node]){
                if(parent[node] != it){
                    parent[it] = node;
                    dist[it] = dist[node] + 1;
                    q.push(it);
                }
            }
        }
        return *max_element(dist.begin(), dist.end());
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        vector<vector<int>> tree1(edges1.size()+1);
        vector<vector<int>> tree2(edges2.size()+1);
        for(auto it : edges1){
            tree1[it[0]].push_back(it[1]);
            tree1[it[1]].push_back(it[0]);
        }
        for(auto it : edges2){
            tree2[it[0]].push_back(it[1]);
            tree2[it[1]].push_back(it[0]);
        }
        int dia1 = diameter(tree1);
        int dia2 = diameter(tree2);
        int wow = ceil((dia1) / 2.0) + ceil((dia2) / 2.0) +1 ;
        return max({wow, dia1, dia2});
    }
};
