class Solution {
public:

    int f(int i, int parent, vector<vector<int>> &adj, int &ans, int up){
        int a=0, b=0;
        for(auto it: adj[i]){
            if(it==parent) continue;
            int k=f(it,i,adj,ans,up+1);
            vector<int> v={k,a,b};
            sort(v.rbegin(),v.rend());
            a=v[0]; b=v[1];
        }
        ans=max(ans,a+b);
        ans=max(ans,up+a);
        return a+1;
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1;
        int m=edges2.size()+1;
        vector<vector<int>> adj1(n);
        vector<vector<int>> adj2(m);
        for(auto it: edges1){
            adj1[it[0]].push_back(it[1]);
            adj1[it[1]].push_back(it[0]);
        }
        for(auto it: edges2){
            adj2[it[0]].push_back(it[1]);
            adj2[it[1]].push_back(it[0]);
        }

        int ans1=0;
        f(0,-1,adj1,ans1,0);

        int ans2=0;
        f(0,-1,adj2,ans2,0);

        cout<<ans1<<" "<<ans2<<"\n";

        int a=ans1/2;
        int b=ans1-a;
        int c=ans2/2;
        int d=ans2-c;

        int di=b+d+1;
        return max({ans1,ans2,di});
    }
};
