template<class Fun> class Y_comb {
    Fun _f;
public:
    template<class T> explicit Y_comb(T &&fun): _f(forward<T>(fun)) {}
    template<class ...Args> decltype(auto) operator()(Args &&...As) {return _f(ref(*this), forward<Args>(As)...);}
};
template<class Fun> decltype(auto) y_comb(Fun &&fun) { return Y_comb<decay_t<Fun>>(forward<Fun>(fun));}


int longestPath(vector<vector<int>> &es) {
    int n=es.size()+1;
    vector<vector<int>> g(n);

    for(auto&e:es){
        g[e[0]].emplace_back(e[1]);
        g[e[1]].emplace_back(e[0]);
    }

    vector<int> d1(n), d2(n), p(n), up(n);
    auto dfs1 = y_comb([&](auto dfs, int u, int fa) -> void {
    	for(auto&v:g[u]) if(v!=fa){
            dfs(v,u);
            int d=d1[v]+1;
            if(d>=d1[u]){
                d2[u]=d1[u],d1[u]=d,p[u]=v;
            }else if(d>d2[u]) d2[u]=d;
        }
    });
    dfs1(0, -1);

    auto dfs2 = y_comb([&](auto dfs, int u, int fa) -> void {
    	for(auto&v:g[u]) if(v!=fa){
            int d = p[u] == v ? d2[u] : d1[u];
            up[v]=max(up[u],d)+1;
            dfs(v,u);
        }
    });
    dfs2(0, -1);

    int ans=1e9;
    for (int i = 0; i < n; ++i) {
        ans=min(ans,max(up[i],d1[i]));  
    }
    return ans;
}

int get(vector<vector<int>> &e){
	int n=sz(e)+1;
	vector<vector<int>> g(n);
	for(auto&x:e){
		g[x[0]].push_back(x[1]);
		g[x[1]].push_back(x[0]);
	}
	int ans=0;
	// vector<int> a(n), mx(n);

    auto dfs1 = y_comb([&](auto dfs, int u, int fa) -> int {
    	int d1 = 0, d2 = 0;
		for (auto v : g[u]) 
			if (v != fa) {
				int d = dfs(v, u) + 1;
				if (d >= d1) {
					d2 = d1, d1 = d;
				}else if(d>d2){
					d2=d;
				}
			}
		ans=max(ans,d1+d2);
		return d1;
    });
    dfs1(0, -1);

	return ans;
}

class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& e1, vector<vector<int>>& e2) {
        auto x=longestPath(e1),y=longestPath(e2);
        n=sz(e1)+1,m=sz(e2)+1;
        auto p1=get(e1),p2=get(e2);
        int c=max({p1,p2,x+y+1});
        return c;
    }
};
