class Solution {
public:
static const    int N=100005;
int n1,n2, c1,c2, d1[N]={0},d2[N]={0};
vector<int> E1[N],E2[N];

void dfs1(int u, int fa) {
  for (int v : E1[u]) {
    if (v == fa) continue;
    d1[v] = d1[u] + 1;
    if (d1[v] > d1[c1]) c1 = v;
    dfs1(v, u);
  }
}

void dfs2(int u, int fa) {
  for (int v : E2[u]) {
    if (v == fa) continue;
    d2[v] = d2[u] + 1;
    if (d2[v] > d2[c2]) c2 = v;
    dfs2(v, u);
  }
}

    int minimumDiameterAfterMerge(vector<vector<int>>& e1, vector<vector<int>>& e2) {
        for(int i=0;i<e1.size();i++){
            E1[e1[i][0]].push_back(e1[i][1]);
            E1[e1[i][1]].push_back(e1[i][0]);
        }
        for(int i=0;i<e2.size();i++){
            E2[e2[i][0]].push_back(e2[i][1]);
            E2[e2[i][1]].push_back(e2[i][0]);
        }
      dfs1(0, -1);
      d1[c1] = 0, dfs1(c1, -1);
    dfs2(0,-1);
        d2[c2]=0,dfs2(c2,-1);
        int ans=max(d1[c1],d2[c2]);
        //cout<<d1[c1]<<" "<<d2[c2]<<"\n";
        ans=max(ans,d1[c1]/2+d2[c2]/2+d1[c1]%2+d2[c2]%2+1);
        return ans;
//      printf("%d\n", d[c]);
    }
};
