class Solution {
public:
    int func(vector<int> adj[],int n){
        vector<int> level(n+1,-1);
        queue<int> q;
        q.push(0);
        level[0]=0;
        while(!q.empty()){
            int node=q.front();
            q.pop();
            for(auto child:adj[node]){
                if(level[child]==-1){
                    level[child]=level[node]+1;
                    q.push(child);
                }
            }
        }
        int maxa=-1;
        int mxnode=-1;
        for(int i=0;i<n;i++){
            if(level[i]>maxa){
                maxa=level[i];
                mxnode=i;
            }
        }
        for(int i=0;i<=n;i++){
            level[i]=-1;
        }
        level[mxnode]=0;
        q.push(mxnode);
        level[mxnode]=0;
        while(!q.empty()){
            int node=q.front();
            q.pop();
            for(auto child:adj[node]){
                if(level[child]==-1){
                    level[child]=level[node]+1;
                    q.push(child);
                }
            }
        }
         maxa=-1;
         mxnode=-1;
        for(int i=0;i<n;i++){
            if(level[i]>maxa){
                maxa=level[i];
                mxnode=i;
            }
        }
        return maxa;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size(),m=edges2.size();
        vector<int> adj1[n+1],adj2[m+1];
        for(auto i:edges1){
            adj1[i[0]].push_back(i[1]);
            adj1[i[1]].push_back(i[0]);
        }
        for(auto i:edges2){
            adj2[i[0]].push_back(i[1]);
            adj2[i[1]].push_back(i[0]);
        }
        int dum1=func(adj1,n+1);
        int dum2=func(adj2,m+1);
        int ans1=(dum1+1)/2;
        int ans2=(dum2+1)/2;
        int ans=ans1+ans2+1;
        ans=max(ans,dum1);
        ans=max(ans,dum2);
        return ans;
    }
};
