class Solution {
public:
    void dfs(int i, vector<int>adj[], vector<int>&vis, vector<int>&dist){
        vis[i]=1;
        for (int ch:adj[i]){
            if (!vis[ch]){
                if (dist[i]+1<=dist[ch]){
                    dist[ch]=1+dist[i];
                    dfs(ch,adj,vis,dist);
                }
            }
        }
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& e1, vector<vector<int>>& e2) {
        int n=e1.size();
        vector<int>adj1[n+1];
        for (int i=0; i<e1.size(); i++){
            adj1[e1[i][0]].push_back(e1[i][1]);
            adj1[e1[i][1]].push_back(e1[i][0]);
        }
        vector<int>dist1(n+1,1e9);
        vector<int>vis1(n+1,0);
        dist1[0]=0;
        dfs(0,adj1,vis1,dist1);
        int node1=0;
        int dist01=0;
        for (int i=0; i<dist1.size(); i++){
            if (dist01<dist1[i]){
                node1=i;
                dist01=dist1[i];
            }
            dist1[i]=1e9;
            vis1[i]=0;
        }
        dist1[node1]=0;
        dfs(node1,adj1,vis1,dist1);
        int ans1=0;
        for (int i=0; i<dist1.size(); i++) ans1=max(ans1,dist1[i]);
        n=e2.size();
        vector<int>adj2[n+1];
        for (int i=0; i<e2.size(); i++){
            adj2[e2[i][0]].push_back(e2[i][1]);
            adj2[e2[i][1]].push_back(e2[i][0]);
        }
        vector<int>dist2(n+1,1e9);
        vector<int>vis2(n+1,0);
        dist2[0]=0;
        dfs(0,adj2,vis2,dist2);
        int node2=0;
        int dist02=0;
        for (int i=0; i<dist2.size(); i++){
            if (dist02<dist2[i]){
                node2=i;
                dist02=dist2[i];
            }
            dist2[i]=1e9;
            vis2[i]=0;
        }
        dist2[node2]=0;
        dfs(node2,adj2,vis2,dist2);
        int ans2=0;
        for (int i=0; i<dist2.size(); i++) ans2=max(ans2,dist2[i]);
        return max(1+(ans1+1)/2+(ans2+1)/2,max(ans1,ans2));

    }
};
