class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size() + 1;
        int m = edges2.size() + 1;
        vector<vector<int>> adj1(n);
        vector<vector<int>> adj2(m);

        for(auto e : edges1){
            adj1[e[0]].push_back(e[1]);
            adj1[e[1]].push_back(e[0]);
        }

        for(auto e : edges2){
            adj2[e[0]].push_back(e[1]);
            adj2[e[1]].push_back(e[0]);
        }



        int max1 = topo(adj1);
        int max2 = topo(adj2);
        int ans = max(max1, max2);

        ans = max(ans, ((max1 + 1)/2) + ((max2 + 1)/2) + 1);
        // cout<<max1<<" "<<max2<<endl;


        return ans;
    }


    int topo(vector<vector<int>>& adj){
        int n = adj.size();

        vector<bool> vis(n);

        queue<int> q;
        q.push(0);
        vis[0] = true;

        // get most far from node 0
        int last;
        while(q.size()){
            auto u = q.front();
            q.pop();
            last = u;

            for(auto v : adj[u]){
                if(vis[v])
                    continue;

                vis[v] = true;
                q.push(v);
            }
        }

        q.push(last);
        vis = vector<bool>(n);
        vis[last] = true;
        int step = -1;

        while(q.size()){
            int sz = q.size();
            ++step;
            while(sz--){

                auto u = q.front();
                // cout<<"step u : "<<step<<" "<<u<<endl;
                q.pop();
                for(auto v : adj[u]){
                    if(vis[v])
                        continue;
                    vis[v] = true;
                    q.push(v);
                }
            }
        }
        // cout<<"last : "<<last<<endl;
        // cout<<"step : "<<step<<endl;
        return step;
    }
};
