class Solution {
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n=edges1.size()+1,m=edges2.size()+1;
        vector<int> adj1[n],adj2[m];
        vector<int> degree1(n,0),degree2(m,0);

        for(auto it:edges1){
            adj1[it[0]].push_back(it[1]);
            adj1[it[1]].push_back(it[0]);
            degree1[it[0]]++;
            degree1[it[1]]++;
        }
        for(auto it:edges2){
            adj2[it[0]].push_back(it[1]);
            adj2[it[1]].push_back(it[0]);
            degree2[it[0]]++;
            degree2[it[1]]++;
        }
        queue<int> q;
        int cnt1=0,cnt2=0,f1=0,f2=0,ans1=0,ans2=0;
        for(int i=0;i<n;i++){
            if(degree1[i]==1){
                q.push(i);
            }
        }

        while(n>2){
            cnt1++;
            int sz=q.size();
            n-=sz;
            while(sz--){
                auto tp=q.front();
                q.pop();
                // cout<<tp<<" ";
                for(auto it:adj1[tp]){
                    degree1[it]--;
                    // if(degree1[it]==-1)f1=1;
                    if(degree1[it]==1){
                        q.push(it);
                    }
                }
            }
            // cout<<endl;
        }
        ans1=2*cnt1;
        if(n==2){
            cnt1++;
            ans1++;
        }
        // cout<<cnt1<<endl;
        while(!q.empty())q.pop();
        for(int i=0;i<m;i++){
            if(degree2[i]==1){
                q.push(i);
            }
        }

        while(m>2){
            cnt2++;
            int sz=q.size();
            m-=sz;
            while(sz--){
                auto tp=q.front();
                q.pop();
                // cout<<tp<<" ";
                for(auto it:adj2[tp]){
                    degree2[it]--;
                    // if(degree1[it]==-1)f1=1;
                    if(degree2[it]==1){
                        q.push(it);
                    }
                }
            }
            // cout<<endl;
        }
        ans2=cnt2*2;
        if(m==2){
            cnt2++;
            ans2++;
        }
        // cout<<cnt2<<endl;
        // cout<<ans1<<ans2;
        return max({ans1,ans2,cnt1+cnt2+1});
    }
};
