class Solution {
    void dfs1(vector<vector<int>>& adj, vector<int>& down, int u, int p) {
        for (int& v: adj[u]) {
            if (v == p) continue;

            dfs1(adj, down, v, u);
            down[u] = max(down[u], down[v] + 1);
        }
    }

    void dfs2(vector<vector<int>>& adj, vector<int>& up, vector<int>& down, int u, int p) {
        int a = -1, b = -1;

        for (int& v: adj[u]) {
            if (v == p) continue;

            int val = down[v];

            if (a < val) {
                b = a, a = val;
            }
            else if (b < val) {
                b = val;
            }
        }

        for (int& v: adj[u]) {
            if (v == p) continue;

            int val = (a == down[v]) ? b + 1 : a + 1;
            int mx = max(val, up[u]);
            up[v] = mx + 1;

            dfs2(adj, up, down, v, u);
        }
    }

    int get(vector<vector<int>>& edges, int& maxi) {
        int n = (int) edges.size() + 1;
        vector<vector<int>> adj(n);

        for (auto& edge: edges) {
            int u = edge[0];
            int v = edge[1];
            adj[u].emplace_back(v);
            adj[v].emplace_back(u);
        }

        vector<int> down(n), up(n);

        dfs1(adj, down, 0, -1);
        dfs2(adj, up, down, 0, -1);
        int res = INT_MAX;

        for (int i = 0; i < n; i++) {
            // cout << i << " " << up[i] << " " << down[i] << "\n";
            int mx = max(up[i], down[i]);
            res = min(res, mx);
            maxi = max(maxi, mx);
        }

        return res;
    }
public:
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int maxi = 0;
        int a = get(edges1, maxi);
        int b = get(edges2, maxi);

        return max(maxi, a + b + 1);
    }
};
