#define vi vector<int>
#define vvi vector<vector<int>>
#define pb push_back
class Solution {
public:
    int bfs1(int src, vvi &adj, int n){
        queue<int>q;
        vi dis(n,-1);
        dis[src] = 0;
        int top;
        q.push(src);
        while(!q.empty()){
            top = q.front();
            q.pop();
            for(int ngbr:adj[top]){
                if(dis[ngbr] == -1){
                    q.push(ngbr);
                    dis[ngbr] = dis[top] + 1;
                }
            }
        }
        return top;
    }
    int bfs2(int src, vvi &adj, int n, vi vis, vi &dis){
        int top;
        queue<int>q;
        q.push(src);
        vis[src] = 0;
        dis[src] = max(dis[src],vis[src]);
        while(!q.empty()){
            top = q.front();
            q.pop();
            for(int ngbr:adj[top]){
                if(vis[ngbr]==-1){
                    q.push(ngbr);
                    vis[ngbr] = vis[top] + 1;
                    dis[ngbr] = max(dis[ngbr],vis[ngbr]);
                }
            }
        }
        return top;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size()+1, m = edges2.size()+1;
        vvi adj1(n,vi(0)),adj2(m,vi(0));
        vi vis1(n,-1), vis2(m,-1);

        for(int i=1;i<n;i++){
            int a = edges1[i-1][0], b = edges1[i-1][1];
            adj1[a].pb(b);
            adj1[b].pb(a);
        }
        for(int i=1;i<m;i++){
            int a = edges2[i-1][0], b = edges2[i-1][1];
            adj2[a].pb(b);
            adj2[b].pb(a);
        }
        vector<int>dis1(n,0),dis2(m,0);
        int diam_1 = bfs1(0, adj1, n);
        int diam_2 = bfs2(diam_1, adj1, n, vis1, dis1);
        bfs2(diam_2, adj1, n, vis1, dis1);

        diam_1 = bfs1(0, adj2, m);
        diam_2 = bfs2(diam_1, adj2, m, vis2, dis2);
        bfs2(diam_2, adj2, m, vis2, dis2);

        int ans1 = 1e9, ans2 = 1e9;
        int max1 = 0, max2 = 0;
        for(int i=0;i<n;i++){
            ans1 = min(ans1,dis1[i]);
            max1 = max(max1,dis1[i]);
        }
        for(int i=0;i<m;i++){
            ans2 = min(ans2,dis2[i]);
            max2 = max(max2,dis2[i]);
        }
        return max({max1,max2,(ans1+ans2+1)});        

    }
};
