class Solution {
public:
    int treeDiameter(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<vector<int>> adj(n);
        for (auto& edge : edges) {
            adj[edge[0]].push_back(edge[1]);
            adj[edge[1]].push_back(edge[0]);
        }


        auto bfs = [&](int start) {
            vector<int> dist(n, -1);
            queue<int> q;
            q.push(start);
            dist[start] = 0;
            int farthestNode = start;
            while (!q.empty()) {
                int node = q.front(); q.pop();
                for (int neighbor : adj[node]) {
                    if (dist[neighbor] == -1) {
                        dist[neighbor] = dist[node] + 1;
                        q.push(neighbor);
                        if (dist[neighbor] > dist[farthestNode]) {
                            farthestNode = neighbor;
                        }
                    }
                }
            }
            return make_pair(farthestNode, dist[farthestNode]);
        };

        auto firstBFS = bfs(0);
        auto secondBFS = bfs(firstBFS.first);
        return secondBFS.second;
    }

    vector<int> findTreeCenters(vector<vector<int>>& edges) {
        int n = edges.size() + 1;
        vector<vector<int>> adj(n);
        vector<int> degree(n, 0);
        for (auto& edge : edges) {
            adj[edge[0]].push_back(edge[1]);
            adj[edge[1]].push_back(edge[0]);
            degree[edge[0]]++;
            degree[edge[1]]++;
        }

        queue<int> leaves;
        for (int i = 0; i < n; ++i) {
            if (degree[i] == 1) {
                leaves.push(i);
            }
        }

        int remainingNodes = n;
        while (remainingNodes > 2) {
            int leavesCount = leaves.size();
            remainingNodes -= leavesCount;
            for (int i = 0; i < leavesCount; ++i) {
                int leaf = leaves.front(); leaves.pop();
                for (int neighbor : adj[leaf]) {
                    if (--degree[neighbor] == 1) {
                        leaves.push(neighbor);
                    }
                }
            }
        }

        vector<int> centers;
        while (!leaves.empty()) {
            centers.push_back(leaves.front());
            leaves.pop();
        }

        return centers;
    }
    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        if(edges1.size()==0 && edges2.size()==0)return 1;
        if(edges1.size()==0 || edges2.size()==0)return 2;
        int d1 = treeDiameter(edges1);
        int d2 = treeDiameter(edges2);

        vector<int> centers1 = findTreeCenters(edges1);
        vector<int> centers2 = findTreeCenters(edges2);

        int minDiameter = INT_MAX;
        for (int c1 : centers1) {
            for (int c2 : centers2) {
                int newDiameter = max({d1, d2, (d1 + 1) / 2 + (d2 + 1) / 2 + 1});
                minDiameter = min(minDiameter, newDiameter);
            }
        }

        return minDiameter;
    }
};
