class Solution {
public:
    pair<int, int> bfs(int start, const vector<vector<int>>& adj) {
        int n = adj.size();
        vector<int> dist(n, -1);
        queue<int> q;
        q.push(start);
        dist[start] = 0;
        int farthest_node = start;
        int max_dist = 0;

        while (!q.empty()) {
            int node = q.front();
            q.pop();
            for (int neighbor : adj[node]) {
                if (dist[neighbor] == -1) {
                    dist[neighbor] = dist[node] + 1;
                    q.push(neighbor);
                    if (dist[neighbor] > max_dist) {
                        max_dist = dist[neighbor];
                        farthest_node = neighbor;
                    }
                }
            }
        }

        return {farthest_node, max_dist};
    }

    int findDiameter(const vector<vector<int>>& adj) {
        pair<int, int> p = bfs(0, adj);
        pair<int, int> q = bfs(p.first, adj);
        return q.second;
    }

    int minimumDiameterAfterMerge(vector<vector<int>>& edges1, vector<vector<int>>& edges2) {
        int n = edges1.size() + 1;
        int m = edges2.size() + 1;

        vector<vector<int>> adj1(n);
        vector<vector<int>> adj2(m);

        for (const auto& edge : edges1) {
            adj1[edge[0]].push_back(edge[1]);
            adj1[edge[1]].push_back(edge[0]);
        }

        for (const auto& edge : edges2) {
            adj2[edge[0]].push_back(edge[1]);
            adj2[edge[1]].push_back(edge[0]);
        }

        int diameter1 = findDiameter(adj1);
        int diameter2 = findDiameter(adj2);

        int height1 = (diameter1 + 1) / 2;
        int height2 = (diameter2 + 1) / 2;

        int new_diameter = max({diameter1, diameter2, height1 + height2 + 1});
        return new_diameter;
    }
};
