class Solution {

    private static class TrieNode {
        public TrieNode[] nexts = new TrieNode[26];
        public int cost = - 1;
    }

    private void insert(TrieNode root , String word , int cost) {
        for (char ch : word.toCharArray()) {
            int idx = ch - 'a';
            if (root.nexts[idx] == null) {
                root.nexts[idx] = new TrieNode();
            }
            root = root.nexts[idx];
        }
        if (cost < root.cost || root.cost < 0) {
            root.cost = cost;
        }
    }

    private int[] dp;

    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0;i < words.length;i ++) {
            insert(root , words[i] , costs[i]);
        }
        dp = new int[target.length()];
        Arrays.fill(dp , - 1);
        for (int i = 0;i < target.length();i ++) {
            TrieNode node = root;
            for (int j = i;j < target.length();j ++) {
                node = node.nexts[target.charAt(j) - 'a'];
                if (node == null) {
                    break;
                }
                if (node.cost >= 0) {
                    int result = node.cost;
                    if (i - 1 >= 0) {
                        if (dp[i - 1] >= 0) {
                            result += dp[i - 1];
                            if (result < dp[j] || dp[j] < 0) {
                                dp[j] = result;
                            }
                        }
                    } else {
                        if (result < dp[j] || dp[j] < 0) {
                            dp[j] = result;
                        }
                    }
                }
            }
        }
        return dp[target.length() - 1];
    }


}
