class Solution {
struct TrieNode {
    unordered_map<char, TrieNode*> children;
    vector<int> wordIndices;
};

class Trie {
public:
    Trie() : root(new TrieNode()) {}

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back(index);
    }

    TrieNode* getRoot() const {
        return root;
    }

private:
    TrieNode* root;
};

public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
    for (int i = 0; i < words.size(); ++i) {
        trie.insert(words[i], i);
    }

    for (int i = 0; i < n; ++i) {
        if (dp[i] == INT_MAX) continue;

        TrieNode* node = trie.getRoot();
        for (int j = i; j < n; ++j) {
            if (!node->children.count(target[j])) break;
            node = node->children[target[j]];

            for (int index : node->wordIndices) {
                int wordLen = words[index].size();
                if (j + 1 >= wordLen) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + costs[index]);
                }
            }
        }
    }

        return dp[n] == INT_MAX ? -1 : dp[n];  
    }
};
